INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Tomatoes', 2, 'Nyanya', 'assets/topic/Tomato.svg', 'Nyanya zimekua zikiliwa tangu zamani. Nyanya ni tunda kutoka mmea wa Mnyanya. Asili yake ni kutoka Marekani ya kati, ilitumika Meksiko ya kusini kwa mara ya kwanza na Aztecs', 'FFB3C8FF');
