INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Tomatoes_7', 1, 'Familia ya Nyanya zenye Afya', 'assets/topic/forest.svg', 'kwa kutumia vifaa vya uchoraji, buni familia ya Watu Nyanya. Chora mikono, miguu, macho, pua, mdomo, uso na masikio. Ongezea ubunifu kufanya Nyanya baba, Nyanya mama, Nyanya kaka, nyanya dada, nyanya bibi, nyanya babu n.k.', NULL);
