INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Tomatoes_9', 3, 'Amini au Usiamini', 'assets/topic/tomatoes_variety_article.jpg', 'Kuna aina 10,000 za nyanya duniani. Haikushangazi?. Umeziona aina ngapi?. Sasa, kama unafikiria Nyanya zina rangi nyekundu tu, hauko sahihi. Nyanya hukua kwa rangi tofauti. Zinaweza kuwa za rangi ya njano, waridi, zambarau, nyeusi na nyeupe. Wakati Nyanya ni salama na Afya kwa kula, majani ya mmea wake ni sumu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Tomatoes_10', 3, 'Matumizi ya Nyanya', 'assets/topic/tomato_consumption.jpg', 'Nyanya huliwa kwa njia tofauti. Inaweza kuliwa kama tunda, saladi, kiungo muhimu cha mboga katika mapishi mbalimbali, mchuzi, supu, achali rojo. Juisi itokanayo na nyanya hutumika kama kinywaji.  Aina za nyanya hujumuisha nyanya globe, nyanya beefsteak, nyanya oxheart, nyanya plum, nyanya cherry na nyanya campari. Je!, uliwahi kufahamu kuwa kuna aina nyingi tofauti za nyanya?.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Tomatoes_11', 3, 'Faida za Kiafya za Nyanya', 'assets/topic/tomato_health.jpg', 'Nyanya ni kiungo kikubwa cha Mlo. Nyanya ina faida kubwa kiafya. Ulaji wa nyanya Unapunguza hatari ya kupata magonjwa ya Moyo na Kansa. Nyanya ni nzuri kwa afya ya macho, ngozi, choo kigumu, ugonjwa wa kisukari. La muhumi zaidi, Nyanya ni tamu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Tomatoes_12', 3, 'Baadhi ya ukweli Muhimu', 'assets/topic/tomatoes_fact.jpg', 'Nyanya zinaweza baki kwa muda mrefu kama zikihifadhiwa na shina lake. Nyanya yenye uzito mkubwa duniani ina kilo 3.51. Mmea mkubwa zaidi wa nyanya unaweza kuzalisha nyanya elfu 32. Si rahisi kuamini!, lakini ni kweli. Nyanya za kijani huiva kama zikihifadhiwa tunda Tofaa. Ulifahamu hilo?. Majani ya mmea wa Mnyanya ni Sumu. "La Tomatino" ni tamasha la Nyanya linalosherehekewa Hispania, kufurahia Nyanya nyingi. Wow! Hiyo inapaswa kushangaza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Cabbage', 2, 'kabichi', 'assets/topic/cabbage.svg', 'Je!, Kabichi halionekani kama ni mkusanyiko wa majani yaliyokusanywa pamoja kama Mpira? Umekisia sawa! Ni kabichi, moja wapo ya mboga za majani zenye Afya. Mboga hizi za Majani zimekua zikizalishwa duniani kwa miaka elfu na zinaweza kupatikana katika aina mbali mbali za milo.', 'FF9DEDE3');
