INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Cabbage_7', 1, 'Nyuso za Kabichi', 'assets/topic/306950.svg', 'Kwa kutumia vifaa vya uchoraji, buni nyuso tofauti za kabichi. Ongeza tabasamu, Kilio, Kuogofya, kicheko na aina yoyote ya hisia kwenye kabichi.', NULL);
