INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Cabbage_9', 3, 'Tuchunguze Mboga za Majani', 'assets/topic/cabbage-salad.jpg', 'Kabichi ni Mboga za majani maarufu duniani. Inaweza kuzalishwa katika hali za hewa na aina za udongo mbalimbali. Ni rahisi kuzalishwa Kabichi na kuhifadhi. Kabichi zina maumbo mbalimbali na rangi tofauti ambazo ni Nyekundu, zambarau, nyeupe na kijani. Majani ya kabichi yanaweza kuwa  magumu au malaini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Cabbage_10', 3, 'Matumizi ya Kabichi', 'assets/topic/cabbage-boiled.jpg', 'Kabichi ni Mboga za Majani manene. Zinaweza kuliwa zikiwa mbichi au zikiwa zimepikwa. Kabichi hutumika katika milo mbalimbali duniani. Kabichi zinaweza kufukizwa, kuchemshwa, kupashwa au kukaangwa. Kabichi zimekua moja kati ya mboga za majani zinazopendwa. Natumaini unapenda kabichi pia!.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Cabbage_11', 3, 'Faida za Kiafya za Kabichi', 'assets/topic/cabbage-health.jpg', 'Kabichi hubeba faidia nyingi kiafya. Ulifahamu kuwa Kabichi ni nzuri kwa mifupa yako?. Ndio, ni kweli. Kabichi husaidia Mifupa kiafya. Kuna uwezekano mdogo wa maambukizi endapo kabichi ikitumika katika milo mara kwa mara. Ulaji wa kabichi ni mzuri katika moyo kusaidia kudhibiti msukumo wa damu. Kabichi pia husaidia kutibu Tumbo na magonjwa katika utumbo mwembamba. Pia huzuia kansa Mwilini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Cabbage_12', 3, 'Baadhi ya ukweli Muhimu', 'assets/topic/red-cabbage.jpg', 'Unafahamu kwamba Kabichi inaweza kupakwa katika vidonda? Hii ni kweli. Upakwaji wa kabichi hutibu vidonda. Kabichi imekua kati ya milo ya bindamu kwa zaidi ya miaka 1,000.  Kabichi haina mafuta kabisa, na imependekezwa kuwa mlo bora. Kabichi nyekundu ina ladha kama kabichi ya kijani. Lakini, Unafahamu kwamba Kabichi Nyekundu huchuja rangi yake inapopikwa na vyakula vingine?. Kabichi Yenye uzito mkubwa ina kilo 62.71 na kumbukumbu yake imehifadhiwa.', NULL);
