INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Onion', 2, 'Kitunguu', 'assets/topic/Onion.svg', 'Vitunguu ni moja kati ya vyakula vya zamani kujulikana na binadamu.Ni mboga za majani zinazotumika sana duniani kote. Ni moja ya familia ya tungule. Hiyo ni kwasababu sehemu kubwa ya umbo la kitunguu ni tungule, huzalishwa chini ya ardhi. Kuna aina 27 tofauti za vitunguu na moja wapo ya zao rahisi kuzalishwa.', 'FFB3C8FF');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Onion_3', 1, 'Ninaweza kuuchora mmea wa kitunguu', 'assets/topic/soil-575643.svg', 'Kwa kutumia vifaa vya uchoraji, chora mmea kuonyesha vitunguu vilivyopo chini ya ardhi.', NULL);
