INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Onion_5', 1, 'Paka kikapu cha Kitunguu mara tatu', 'assets/topic/basket-2075011.svg', 'Chora kikapu kilichojaa vitunguu. Paka vitunguu rangi ya zambarau, njano, na nyeupe.', NULL);
