INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Onion_7', 1, 'Shamba la Konokono kitunguu', 'assets/topic/snail-48177.svg', 'Angalia katika picha ya konokono. Kwa kutumia vifaa vya uchoraji, buni shamba la Konokono kitunguu. Paka vitunguu rangi upendazo. Zifanye zipendeze vikizungukwa na konokono.', NULL);
INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('Onion_7', 2, 1, 'assets/topic/snail-48177.svg');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Onion_9', 3, 'Tuchunguze mboga za majani', 'assets/topic/market-onions.jpg', 'Kilimo cha vitunguu kilianza miaka 7,000 iliyopita. Ni ukweli kwamba vitunguu hutumika duniani kote. Vitunguu hutofautiana ukubwa, umbo, rangi, na ladha. Mbali na Ladha yake, pia huongeza faida za afya mwilini. Vitunguu hutoa mizizi, tungule na majani ya kijani. Majani na tungule hutumika, kasoro mizizi. Vitunguu huweza kuwa vya mviringo au umbo la yai. Humea katika rangi ya zambarau, nyeupe na njano. Kitunguu Pearl, Kitunguu Bermuda, Kitunguu egyptian, kitunguu cha kijani, kitunguu vidalia, kitunguu super-sweets, kitunguu shallots na kitunguu scallions ni aina mbalimbali za vitunguu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Onion_10', 3, 'Faida za kiafya za Kitunguu', 'assets/topic/perspective-onion-7.jpg', 'Vitunguu huweza kuliwa vikiwa vibichi, vikiwa vimepikwa au kama achari. Vitunguu husifiwa kwa faida zake za kiafya za kiajabu. Vitunguu ni vizuri, kwa macho, vizuri kwa moyo, husafisha figo, hupunguza kuvimba kwa mwili, hurahisisha mmengényo wa chakula na kupunguza maumivu ya mwili. kwa ujumla, vitunguu mlo wenye Afya na hatuna budi kukubali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Onion_11', 3, 'Baadhi ya ukweli muhimu', 'assets/topic/chopped_onion.jpeg', 'Ukataji wa vitunguu husababisha macho kutoa machozi. Ni kwasababu unavihurumia vitunguu?. Hapana, hapana kabisa. Vitunguu vina asidi ya sulfuriki inayounguza macho yako. Usihofu, sio hatari kula. Unafahamu kwamba kuweka kipande cha kitunguu kilichopikwa juu ya kifua chako na ukajifunika na blanketi itakusaida kuondoa homa na kikohozi?. Kwa hiyo sasa una dawa ya papo kwa hapo nyumbani. Asilimia 97 ya vitunguu ni maji. Sasa unafahamu kwanini ni vizuri kwa Afya! Kitunguu kikubwa kuvunwa kina uzito wa kilo 5. Vitunguu vya njano ni aina ya vitunguu maarufu. Usipuuzie hili, Vitunguu huweza kuua mbwa. Hupunguza chembe hai za damu na mzunguko wa damu. Hii huweza kuua Mnyama.', NULL);
