INSERT INTO `card` (id, type, title, header, content, option) VALUES ('banana', 2, 'Ndizi', 'assets/topic/banana.svg', 'Ni nani asiyetamani tunda kizingo lenye nyama liitwalo ''Ndizi''. Ukweli ni kwamba ndizi hujulikama kama tunda la wote. Ndio, Kuna wanadamu wachache duniani ambao hawapendelei kula ndizi. Ndizi zina Afya na ladha. Sio kwa Binadamu tu, hata Tembo na Nyani hupenda kula ndizi pia.', 'FF9DEDE3');
