INSERT INTO `card` (id, type, title, header, content, option) VALUES ('pineapple', 2, 'Nanasi', 'assets/topic/Pineapple.svg', 'Kila mtu hukubali pale nanasi linapoitwa tunda lenye ncha kali. Ni kweli na ni tunda ala ajabu lenye taji ambalo lina ncha kali. Huogopesha kununua Nanasi. Ncha hizo ndogo zimezunguka tunda lote, ni kali na zinauma zinapokuchoma. Lakini, tukubali kwamba ni tunda lenye ladha na wote tunapenda kula.', 'FFB3C8FF');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('pineapple_3', 1, 'Hey! Unalionaje tunda langu lenye ncha kali?', 'assets/topic/table-for-keeping-banana.svg', 'Kwa kutumia vifaa vya uchoraji, chora Nanasi. Ongeza rangi uzipendazo.', NULL);
