INSERT INTO `card` (id, type, title, header, content, option) VALUES ('pineapple_7', 1, 'Jack-o-lanterns', 'assets/topic/pineapple-304567.svg', 'Kwa kutumia vifaa vya uchoraji, buni mashimo ya macho na mdomo katika nanasi ulilopewa. Paka rangi na ongezea ubunifu kuunda taa ya kutisha.', NULL);
