INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Auto rickshaw_9', 3, 'Mkokoteni', 'assets/topic/pulled_rikshaw2.jpg', 'Chombo maarufu cha abiria barabarani ni Bajaji. Bajaji kiasilia ilitengenezwa kama gari ya abiria ya matairi mawili au matairi matatu. Sasa inajulikana kama mkokoteni wa kuvutwa. Mkokoteni wa kuvutwa Kiujumla ulivutwa na mtu mmoja ukiwa umebeba abiria mmoja. Muda ulivozidi kwenda Mikokoteni ya matairi iliyojulikana kama mikokoteni ya kusukumwa ilipelekea bajaji na mikokoteni ya umeme kuvumbuliwa. Si rahisi kukataa kuwa, mabadiliko mapya yaliwasaidia watalii.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Auto rickshaw_10', 3, 'Mkokoteni wa kuvutwa.', 'assets/topic/pulled_rikshaw.jpg', 'Katika siku za mwanzo za ugunduzi, mikokoteni ya kuvutwa iliunda muundo wa usafiri maarafu. Ilikua pia ni chanzo cha ajira kwa wanaume. Lakini wingi wake ulipungua baada ya Magari, Gari moshi, na vyombo vingine vya usafiri kupatikana kwa wingi. Ukweli ni kuwa mikokoteni ya kusukumwa haitumiki tena kwenye m,iji mikubwa. Lakini katika miji midogo bado wanatumia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Auto rickshaw_11', 3, 'Bajaji', 'assets/topic/auto.jpg', 'Bajaji ni muundo mpya wa mikokoteni ya kuvutwa. Bajaji zina matairi matatu na mota. Bajaji ni maarufu kwa shughuli za usafiri mijini. Bajaji hutumiwa kwa kukodiwa na kwa matumizi binafsi. Hizi bajaji zinatumika sana katika miji mingi duniani. India ni watengenezaji wakubwa wa bajaji duniani. Bajaji zilikua zikiendeshwa kwa kutumia Mafuta ya Petrol, na kwa sasa zinaendeshwa kwa kutumia mafuta ya Dizeli. Uundwaji Mpya umekamilika ambapo bajaji zitaendeshwa kwa umeme, zitakazojulikana kama bajaji za umeme. Bajaji za Nishati ya Jua pia zipo mbioni kutengenezwa. Teknolojia nzuri!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Auto rickshaw_12', 3, 'Bajaji nchini Tanzania', 'assets/topic/auto_tanzania.jpg', 'bajaji zinajulikana kwa jina la Kingereza Auto Rickshaw. Bajaji ni moja ya chombo cha usafiri mjini Dar es salaam. Bajaji huitwa pia "Teksi mtoto" na zinaendeshwa pia kwa Gesi inayojulikana kwa kingereza Compressed natural gas (CNGs). Ni moja kati ya usafiri maarufu Tanzania. Inajulikana kwa ukubwa na spidi yake ambayo huisadia kwenda umbali mrefu, pia kupita vizuri hata kwenye sehemu yenye msongamano.', NULL);
