INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Tractor', 2, 'Trekta', 'assets/topic/tractor.svg', 'Trekta ni chombo cha kufanyia kazi imara kwaajili ya kilimo. Kwa kawaida ina kiti kimoja tu kwaajili ya dereva. Trekta hutumika kuvuta vifaa vingi mbali mbali au matrela', 'FF9DEDE3');
