INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Tractor_7', 3, 'Gari la shamba', 'assets/topic/Tractor-on-farm.jpg', 'Trekta ni chombo kitumikacho shambani. Trekta la shambani hutumika kuvuta au kusukuma mashine za kilimo au matrela.Pia hutumika kwaajili ya kulimia, kupalilia, kuvunia,  kusafishia shamba, kupandia na shughuli zinazofanana na hizo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Tractor_8', 3, 'kwanini trekta ni Muhimu sana?', 'assets/topic/Tractor_working_Farm.jpg', 'Trekta ni mashine ya kimsingi ambayo hutoa nguvu kazi kwaajili ya shughuli za kilimo. Trekta huweza kuvuta vifaa mbalimbali vya kilimo kwaajili ya kulimia, kupandia, Kukuzia na uvunaji mazao. Pia huweza kutumika kubeba vifaa na kwa usafiri binafsi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Tractor_9', 3, 'Nafasi ya nguvu za binadamu zimechukuliwa na trekta', 'assets/topic/Farming-Agriculture-Tractor.jpg', 'Ndani ya miongo mifupi michache, trekta ilikuwa haitumiki sana. Baadae wakulima waligundua faida za kilimo cha kutumia trekta. Hakukuwa na uhitaji tena wa wafanyakazi wakati mashine rahisi kama trekta inaweza kufanya kazi hiyo. Hii iliokoa muda sana na gharama. Je hicho sio chombo cha kazi cha maaajabu?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Tractor_10', 3, 'gharama za awali zilikua kubwa sana', 'assets/topic/tractor-pulling-farm.jpg', 'Awali bei ya trekta ilikuwa kubwa sana. Lakini ndani ya miaka miwili ya uvumbuzi wake, bei ilishuka karibu nusu yake. Trekta ilikuwa nafuu kumilikiwa kwaajili ya kilimo karibu na kila mkulima. Hii ni kwasababu uzalishaji wake uliongezeka. Mahitaji yake na gharama zilikuwa juu. Na hivyo kuuzwa kwa bei nafuu. Kiukweli inawezekana ikaitwa rafiki wa mkulima.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Tractor_11', 3, 'Trekta na vifaa vyake.', 'assets/topic/tractor-farm-equipment.jpg', 'Trekta, katika kilimo inatumika kuvuta vifaa kama Jembe, Vifaa vya kupandia na Kufyekea. Husaidia sana kuendesha vifaa imara kama kama misumeno na winchi.Trekta pia hutumika kuondolea vizuizi vilivyopo barabarana kama mawe makubwa, magogo n.k. Matrekta ya awali yaliendeshwa kwa kutumia Injini za mvuke na sasa yanaendeshwa kwa kutumia injini za petroli na dizeli. Aina kuu mbili za trekta ni trekta ya matairi na trekta ya mitambo, ambayo hutembea kwa Mnyororo wa vipingilipingili.', NULL);
