INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Airplane', 2, 'Ndege', 'assets/topic/plane.svg', 'Umeshawahi kuona kitu chochote kinachotembea angani kikiwa na mabawa makubwa mawili? Hufanana na kiumbe ndege lakini sio kiumbe ndege. Ndio, umekisia kwa usahihi. Ni ndege. Pamoja na kuwa ni usafiri wa haraka, ndege huonyesha mandhari nzuri angani.', 'FF9DEDE3');
