INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Train_9', 3, 'Ukweli kuhusu gari moshi', 'assets/topic/train_at_Station.jpg', 'Mahali ambapo Gari Moshi husimama na watu hupanda na kushuka panaitwa Stesheni ya Gari moshi. Natumaini wote mmeona! Gari Moshi za zamani zilitumia injini ya nguvu ya farasi kutembea kwenye reli za mbao au chuma. Unafahamu kwamba gari moshi ya injini ya mvuke ya kwanza ilitengenezwa Uingereza! Kuna injini ya reli inayosukuma mabahewa kwenye njia yake. Baadhi ya Gari Moshi huonekana kama mabasi maalumu ambayo yanaweza kuendeshwa kwenye reli. Gari Moshi hutumika kubeba watu pia kuingiza na kutoa mizigo kama mafuta na kadhalika.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Train_10', 3, 'Mizigo ya gari Moshi', 'assets/topic/goods-train.jpg', 'Gari Moshi za mizigo zina Mabehewa imara yanayoweza kubeba mizigo kutoka sehemu moja kwenda nyingine. Baadhi ya mabehewa huwa wazi au hufungwa kulingana na mizigo inaosafirishwa kutoka sehemu moja kwenda sehemu nyingine pia kuna behewa maalumu kwaajili ya mizigo maalumu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Train_11', 3, 'Abiria wa Gari Moshi', 'assets/topic/passenger-train.jpg', 'Gari Moshi za abiria huwa na mabehewa ya abiria au makochi yaliyotengenezwa ili watu waweze kukaa na kuendeshwa kutoka kituo kimoja hadi kingine. Gari Moshi za abiria huwa za kustarehesha kwaajili ya safari kutoka sehemu moja hadi nyingine. Kuna abiria wa Gari Moshi za safari ndefu na safari fupi. Na ulimwengu wa sasa tunaona Gari Moshi za abiria zenye mwendo kasi ambazo zinaitwa  Gari Moshi risasi. Gari Moshi hizi hutembea katika reli maalamu.', NULL);
