INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Trucks_13', 3, 'Lori ni nini?', 'assets/topic/trainloaded.jpg', 'Lori ni gari zito la barabarani ambalo huja katika maumbo na ukubwa tofauti tofauti. Lori hizi hutumika kulingana na ukubwa wa mzigo unaohitajika kusafirishwa kutoka sehemu moja kwenda sehemu nyingine. Iliitwa lori kutokana na uwezo wake wa kufanya kazi kubwa na nzito.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Trucks_14', 3, 'Aina za Lori', 'assets/topic/trucktype.png', 'Kuna aina tofauti za lori kulingana na mahitaji. Lori ndogo ni lori ambazi zina ukubwa sawa na magari ya kawaida. Huwa yanamilikiwa na mtu mmoja au kampuni ndogo. Lori za saizi ya kati ni zito kuliko lori ndogo na mepesi kuliko lori kubwa zinzotembea barabarani. Lori hizi ni maalumu kwa matumizi ya madogo madogo na shughuli za Umma kama magari ya Taka. Lori kubwa ni zito, hujulikana kwa kingereza kama "Lorries". Mara nyingi Lori kubwa huvuta trela. Madereva wa wanaondesha lori kubwa huitwa Madereva Lori.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Trucks_15', 3, 'Lori la Austalia', 'assets/topic/austtruck.jpg', 'Unaweza kukisia ni sehemu gani ya dunia kuna Lori kubwa kuliko yote dunian? Na jaribu kufikiria linaonekanaje?. Lori kubwa linapatikana Australia na yapo tofauti sana na lori zingine. Hizi lori huitwa Gari-moshi-lori za Australia. Idadi kubwa ya trela hufungwa kwenye lori lenye Injini.  Hii inafanana na Gari moshi na linaonekana kama gari moshi likiwa barabarani. Lori halishangazi? Ndio, Ni gari moshi zuri lililoundwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Trucks_16', 3, 'Mwanasesere Lori', 'assets/topic/toytruck.jpg', 'nani hapendi kucheza na mwanasesere Lori.inaweza kuwa watoto ama watu wazima, kila mtu anapenda kucheza na Lori. Kuna makampuni mengi yanayotengeneza aina mbalimbali tofauti za wanasesere lori. Unaua jinsi ya kucheza na mwanasesere Lori? Ndio, Inategemea na fikra zako. kujaza mchanga, udongo na mawe, kupakua na kupakia vitu wanasesere wadogowadogo n.k . Sasa, tumia fikra zako na ufurahie kucheza kujaza mwanasesere Lori.', NULL);
