INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Fisherman_9', 3, 'Maisha ya Mvuvi', 'assets/topic/Fisherman_life.jpg', 'Umewahi kujiuliza ni wapi wavuvi huvua samaki? Na maisha yao yapoje?. Ngoja nikuambie. Maisha ya Mvuvi. Mvuvi ni mtu ambaye kazi yake ni kuvua samaki kwenye mabwawa, mifereji na hata kwenye mito kisha huuza kwenye masoko ya ndani. Taaluma yake kuu ni kuvua samaki na kuuza ili aweze kuishi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Fisherman_10', 3, 'Faida za Mvuvi', 'assets/topic/Fisherman_02.jpg', 'Mvuvi huvua samaki mara kwa mara kufanya Uvuvi wa faida ambao huboresha chakula na kulinda lishe. Pia huongeza kipato ambacho huboresha maisha ya Mvuvi, kukuza uchumi na kulinda mazingira yetu na mali asili', NULL);
