INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Fisherman_14', 3, 'Ndoano ya kuvulia', 'assets/topic/Fisherman_03.jpg', 'Unazijua mbinu za Uvuvi?. Huzijui?. Basi, ngoja nikuambie, Mbinu za uvuvi ni neno ya ujumla linalojuimuisha vifaa anavyotumia mvuvi wakati anapovua samaki. Karibu vitu vyote anavyotumia Mvuvi huitwa Mbinu za uvuvi. Baadhi ya Mifano ni Ndoano, Kamba, Wazamiaji, Mikuki, Wavu, Mitego.', NULL);
