INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Farmer', 2, 'Mkulima', 'assets/topic/farmer.svg', 'Mkulima ni Mtu ambae hupanda mazao na kukuza wanyama kwaajili ya matumizi ya Binadamu. Wakulima wanatakiwa wafanye kazi kwa bidii na kwa muda mrefu bila kujali ni kiangazi au masika. Mkulima hufanya kazi kwa bidii ili kupata mazao mengi.', 'FFF4E1B5');
