INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Farmer_20', 3, 'Aina za wakulima', 'assets/topic/farming.jpg', 'Kuna aina nyingi za kilimo lakini leo tunaenda kujifunza aina tatu kuu za kulimo. Kuna aina tatu za kilimo ambazo ni kilimo cha zao moja, kilimo cha ufugaji na kilimo cha mazao mchanganyiko. Katika kilimo cha zao moja, mkulima hupanda zao moja katika msimu wa mavuno, mfano kama Ngano ilipandwa msimu uliopita wa mavuno, msimu unaofuata atapanda mchele. Katika Kilimo cha mazao mchanganyiko kinachofanyika ni kwamba, Mkulima hupanda mazao mawili au zaidi ya aina tofauti katika shamba moja katika msimu mmoja wa mavuno, mfano Ngano, choroko hupandwa katika shamba moja. Kilimo cha Ufugaji ambacho huitwa kilimo cha wanyama ni uzalishaji wa mazao ya wanyama. Mfano kiwango kikubwa cha Ngómbe huzalishwa kwaajili ya kupata maziwa. Na ufugaji wa ndege kama kuku, bata, bata mzinga ni kwaajili ya kupata nyama na Mayai yao.', NULL);
