INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Farmer_32', 3, 'Aina za mbegu mkulima apandazo na mali asili', 'assets/topic/seeds.jpg', 'Mahindi, mtama, mchele, Ngano, Viazi n.k. Haya ni aina ya mazao ya msingi ambayo mkulima huvuna. Ili kuwe na uzalishwaji mzuri wa mazao, Mkulima anapaswa alime kwenye udongo mzuri, kuwe na maji ya kutosha na Mwanga wa jua. Mali asili hizi tatu humsaidia mkulima kupata mazao mazuri.', NULL);
