INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Teacher_9', 3, 'Kuhusu Mwalimu', 'assets/topic/teacher_02.jpg', 'Mwalimu ni mtu anayesaidia watu kujifunza. Mara nyingi Mwalimu ni mtu anayefanya kazi darasani. Kuna aina nyingi tofauti za walimu.Baadhi ya walimu hufundisha watoto wadogo katika shule za awali na shule za msingi. Baadhi hufundisha watoto wakubwa katika shule za sekondari. baadhi ya walimu hufundisha watu wazima katika shule za elimu ya juu (mfano, vyuo)', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Teacher_10', 0, 'Mara nyingi Mwalimu fanya kazi wapi?', 'assets/topic/classroom.jpg', NULL, 'oneAtATime');
