INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Teacher_15', 3, 'Njia ya ufundishaji ya Walimu', 'assets/topic/teaching_way.jpg', 'Kuna aina tofauti za ufundishaji. Walimu wengi hutumia mbinu mbalimbali kufundisha. Walimu wa zamani walitumia vipande vya chaki kuandikia katika ubao mweusi lakini walimu wa sasa hutumia kalamu kuandikia katika ubao mweupe ambazo ni rahisi kufutika. Mara nyingi walimu hueleza maarifa mapya, huandika kwenye ubao mweusi au ubao mweupe, hukaa nyuma ya madawati yao au kwenye viti vyao, kusaidia wanafunzi katika kazi zao. Huweza kutumia kompyuta kuandika majaribio, kazi za darasani au matokeo. Huwapa wanafunzi "Kazi za nyumbani" na kumfuatilia kila mwanafunzi, ili kuwawezesha kutimiza ndoto zao.', NULL);
