INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Teacher_24', 3, 'Dhamani ya Mwalimu kwa Mwanafunzi', 'assets/topic/teacher_03.jpg', 'Mwalimu wa shule ni mtu aliyefunzwa kuelimisha watoto kuanzia umri mdogo mpaka kufikia utu uzima na kila hatua hufundishwa na walimu tofauti. Wana wajibu katika elimu na ukuaji wa watoto katika darasa. Walimu bora huunda uhusiano mzuri na wanafunzi wao, huwaonyesha kuwajali na kuwafundisha masomo yahusuyo maisha na kuwafanya wawe watu wazuri. Walimu bora ni wale ambao huwapa vipaumbele wanafunzi wao, hutoa vyanzo, na kuwapa mapendekezo wazazi wao, kuweza kujua mapungufu na uimara wa wanafunzi wao.', NULL);
