INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Teacher_33', 3, 'Sifa za Walimu.', 'assets/topic/teaching_12.jpg', 'Sifa kuu tano za walimu bora, kulingana na wafunzi ni: uwezo wa kuimarisha uhusiano wao na wanafunzi wao. Uvumilivu, kujali, na ukarimu. Kuwatambua wanafunzi wao humaanisha, mwalimu awe na uwezo wa kumuelewa mwanafunzi wake. Kujitolea kufundisha. Kushirikisha wanafunzi katika kujifunza.', NULL);
