INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Doctor_9', 3, 'Kuhusu Mganga wa Afya', 'assets/topic/doctor_02.jpg', 'Watoto, mnafahamu kuwa kila tunapougua, tunapopata homa, mafua au kukohoa, huwa tunaenda kwa Mganga wa Afya. Mganga wa Afya ni mtu anayetoa dawa  na amefunzwa kutunza afya za watu na kuwatibu wanaougua. Kuna sharti nyingi ambazo Mganga wa Afya huangalia kabla ya kutoa matibabu kwa mgonjwa. Kwanza huhitaji kutambua ni kipi kinachosababisha mtu augue kisha huamua ni matibabu gani apatiwe mgonjwa. Mganga wa Afya hubashiri ni wakati upi mgonjwa atapata ahueni. Ni kwamba Mganga wa Afya hujaribu kuzuia Magonjwa. Kuna watu wengi ambao husaidia Waganga wa Afya na kuikamilisha Hospitali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Doctor_10', 0, 'Tukiugua tunaenda kwa nani?', 'assets/topic/doctor.jpg', NULL, 'oneAtATime');
INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('Doctor_10', 1, 1, 'Kwa Mganga wa Afya');
INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('Doctor_10', 0, 1, 'Fundi Bomba');
INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('Doctor_10', 0, 2, 'Mwalimu');
INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('Doctor_10', 0, 3, 'Mshonaji');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Doctor_15', 3, 'Hospitali', 'assets/topic/hospital.jpg', 'Hospitali ni Mahali ambapo mtu huenda kupata matibabu wakati anapokua anaugua au kuumia. Waganga wa Afya na wauguzi wanafanya kazi hospitali. Waganga wa Afya hutumia teknolojia ya kisasa kutibu wagonjwa. Pia Hospitali hutoa hudai malipo ya matibabu na uchunguzi. Kuna watu wengi wanaofanya kazi Hospitali kama Waganga wa Afya, Wauguzi na wafanyakazi wa dawa mbalimbali. Pia Hospitali inahusisha Magari ya Wagonjwa ambayo hutumika kuwafuata wagonjwa wakati wa dharura na kuwabeba, kisha kuwafikisha Hospitali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Doctor_16', 0, 'Waganga wa Afya hawafanyi kazi Hospitali.', 'assets/topic/doctors_img.jpg', NULL, 'oneAtATime');
