INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Doctor_19', 3, '"Kipima mapigo ya moyo"', 'assets/topic/stethoscope.jpg', 'Kipima mapigo ya moyo n kifaa kinachotumika na Waganga wa Afya na wafanyakazi wengine wa dawa. Hutumika kupima mapigo ya moyo ya watu na kasi ya upumuaji. Huwekwa katika eneo ambalo kuna moyo au mgongoni. Kipima Mapigo ya Moyo kina umbo kama la herufi Y. "Vipande vya sikio" vimewekwa katika kila mwisho wa herufi Y na kinasa sauti katika upande mwingine.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Doctor_20', 0, 'Nini hutumika kupima watu mapigo ya moyo?', 'assets/topic/stethoscope.jpg', NULL, 'oneAtATime');
