INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Doctor_28', 3, 'Gari la wagonjwa', 'assets/topic/ambulance.png', 'Mara nyingi inapotokea Mgonjwa anaumwa sana au kaumia sana na Mganga wa Afya hawezi kufika eneo alipo Mgonjwa kwasababu ya kuhudumia wagonjwa wengine, ndipo Gari la Wagonjwa hutolewa kumbeba Mgonjwa na kumfikisha Hospitali, ambapo Mganga wa Afya humpatia Mgonjwa matibabu ya haraka, kwasababu kuna vifaa vingi vya matibabu kuokolea maisha ya Mgonjwa. Kwa njia hii Gari la Wagonjwa hufanya kazi kubwa ya Muhimu kunusuru maisha ya Wagonjwa kwa kuwaleta kwa Mganga wa Afya.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Doctor_29', 0, 'Taja jina la gari la hospitali litumikalo kubebea wagonjwa wakati wa dharura?', 'assets/topic/ambulance.png', NULL, 'oneAtATime');
