INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('Barber_7', 2, 1, 'assets/topic/barber_svg3.svg');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Barber_9', 3, 'Kuhusu Kinyozi', 'assets/topic/barber_02.jpg', 'Kinyozi ni mtu anayenyoa nywele na kuchonga ndevu. Kawaida wanaume ndo huenda saluni. Wanawake mara nyingi huenda kwa mtengeneza nywele. Wakati mwingine Vinyozi huwa na nguzo zenye rangi nyeupe na nyekundu nje ya saluni zao katika nchi nyingi. Kazi ya kinyozi ni kazi ya zamani na ni taaluma ya heshima, yenye historia ya kuvutia. Kazi ya kinyozi ni kunyoa au kupunguza nywele. Ukiachana na kunyoa nywele, Kinyozi pia huosha na kuziweka nywele katika miundo tofauti, mara nyingi ni kwaajili ya wateja wa kiume. Vitu vinavyopatikana saluni ni mikasi, viwembe, vitana, poda, kioo, mafuta, etc', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Barber_10', 0, 'Ni nani hukata nywele na kuchonga Ndevu?', 'assets/topic/barber.jpg', NULL, 'oneAtATime');
INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('Barber_10', 1, 1, 'Kinyozi');
