INSERT INTO `cardExtra` (cardId, type, serial, content) VALUES ('Barber_10', 0, 3, 'Mshonaji');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Barber_15', 3, 'Maelezo ya kazi', 'assets/topic/barber_03.jpg', 'Kazi kuu ya kinyozi ni kunyoa nywele za mteja wake. Malengo huwa ni kuboresha muonekano wa nywele na kufanya muuondo wa nywele kulingana na mteja wake apendavyo. Kinyozi pia huunda na kunyoa ndevu na sharubu. Vifaa kama Mikasi, Vitana, vinapaswa viwekwe safi kabisa na katika hali nzuri. Kinyozi pia anawajibika kuweka saluni safi kwa kufagia nywele. Wamiliki wa saluni pia huagiza vifaa, hulipa bili na huweza kuajiri wafanyakazi. Ujuzi mzuri wa kipekee na mtazamo chanya ni sifa muhimu ambazo kinyozi anapaswa kuwa nazo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Barber_16', 0, 'Kifaa kikuu cha Kinyozi ni kipi?', 'assets/topic/scissors.png', NULL, 'oneAtATime');
