INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Barber_35', 3, 'Historia', 'assets/topic/barber_04.jpg', 'Biashara ya Kinyozi ina historia ndefu. Nyembe zimegundulika Misri, miongoni mwa masanduku ya enzi za shaba. Katika utamaduni wa kale wa Misri, Vinyozi walikua wakiheshimika sana, kila mmoja. Makuhani/Mapadri na wanaume wa dawa ni kumbukumbu ya zamani ya mifano ya Vinyozi. katika makabila ya zamani, Kinyozi alikua ni mmoja wa watu muhimu sana wa kabila. Vinyozi waliweza hata kufanya sherehe za dini, kama sherehe za Ndoa na kubatiza watoto kwa kuwanyoa nywele na kuwachonga ndevu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Barber_36', 0, 'Ni nini kiligunduliwa Misri kipindi cha Shaba?', 'assets/topic/razors.png', NULL, 'oneAtATime');
