INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Barber_41', 3, 'Mtengeneza Nywele', 'assets/topic/female_hair_dresser.jpg', 'Kihistoria, watengeneza nywele wote walihesabika kama Vinyozi. Kazi ya Mtengeneza Nywele ni kupunguza nywele katika muundo fulani na pia kutumia mapambo ili kumfanya mtu apendeze. Wanaweza kupunguza nywele, kuongeza rangi. Mtengeneza nywele mara nyingi huwa ni wa kike. Mtengeneza nywele wa kiume ambaye hupunguza nywele za wanaume huitwa Vinyozi. Mara nyingi hutumia muda mwingi kenye shule za urembo kuimara ujuzi wao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Barber_42', 0, 'Mtengeneza Nywele mara nyingi huwa ni mwanamke.', 'assets/topic/hairdresser.jpg', NULL, 'oneAtATime');
