INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Fisherman', 2, 'Fisherman', 'assets/topic/fisherman.svg', 'A fisherman or fisher is someone who catch fish and other water animals for their living or for sport.', 'FFB3C8FF');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Engineer', 2, 'Engineer', 'assets/topic/engineer.svg', 'Engineers are people who design or build things that we use every day.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Farmer', 2, 'Farmer.', 'assets/topic/farmer.svg', 'A farmer is  an individual who grows crops and raises animals for human use. Farmers work hard and for long hours. They work in all season and all weather conditions.', 'FFB3C8FF');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Teacher', 2, 'Teacher', 'assets/topic/teacher.svg', 'A teacher is an individual who is trained to teach children. A teacher teaches from early stage of the children to their adult stage.', 'FFB3C8FF');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Doctor', 2, 'Doctor', 'assets/topic/doctor.svg', 'A Doctor is a person who gives treatment to sick people. The doctor prescribes the medicine for the sick people. The doctor advices precautionary measures to lead a healthy life.', 'FFCFB5DD');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Barber', 2, 'Barber', 'assets/topic/barber.svg', 'A barber is a person whose occupation is mainly to cut, dress, groom, style and shave men''s and boys'' hair.', 'FF9DEDE3');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Plumber', 2, 'Plumber', 'assets/topic/plumber.svg', 'A plumber is a person whose main job is to connect and repair things such as water and drainage pipes, baths and toilets.', 'FFCFB5DD');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Police', 2, 'Police officer', 'assets/topic/police.svg', 'A police officer is a person who is working in a police force in order to maintain peace among people.', 'FF9DEDE3');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('House wife', 2, 'House wife', 'assets/topic/housewife.svg', 'A house wife''s main role is to take care of her own family that includes her home, her children, and her husband mainly.', 'FFB3C8FF');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Carpenter', 2, 'Carpenter', 'assets/topic/carpenter.svg', 'A carpenter is a person who works with wood and make wooden furnitures like cots, cabinets, build houses, or do other useful things.', 'FFB3C8FF');
