INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Fischer''s lovebird_28', 3, 'Kupata kujua Tabia', 'assets/topic/lovebirdArticle_6.jpg', 'Cherero Machungwa huonyesha ushikiriano wa kijamii zaidi. Mara kwa mara huonekana wakiwa wawili wawili. Kama Cherero Machungwa wakitengana kutoka kwenye makundi yao huuzunika. Cherero Machungwa hupaa kwa kasi. Wana kasi sana kiasi kwamba mabawa yao husikika wakati wakipaa. Viumbe hivi vina rundo la sauti mbalimbali. Huendelea kuimba na kupiga filimbi siku nzima. Sauti zao husikika vizuri zaidi nyakati za asubuhi na jioni. Sauti zao nyembamba huvutia kusikika. Ni ndege wa kimakundi na hupendelea kuzungumza kwa pamoja. je! hiyo sio tabia ya kuvutia?', NULL);
