INSERT INTO `card` (id, type, title, header, content, option) VALUES ('African Safari Ant_20', 3, 'Mzunguko wa maisha ya Siafu hodari', 'assets/topic/safariAnt_9.jpg', 'Siafu Safari wanajulikana kama canivora. Hawa siafu wanajulikana kuwa wawindaji hodari. Wanatumia Idadi yao kuua Windo lao. Mgawanyo wa chakula unapokua mdogo, wanaondoka kwenye kichuguu na kuunda msafara unaoongozana wa Siafu 50,000,000. Hili ni tatizo haswa kwa watu. hususani kwa msafara kama huu kwenda majumbani, ni tatizo kubwa sana. Ni hatari kwa mtu asiyeweza kutoka kwenye njia yao. kwa kutumia taya zao kali zinazokata, hawa Siafu hushambulia kila kinachokatiza mbele yao. Inaweza kuwa Nyoka, Ndege, Mamalia na hata Binadamu. Ni wakali sana.', NULL);
