INSERT INTO `card` (id, type, title, header, content, option) VALUES ('African Safari Ant_29', 3, 'Ukweli muhimu wa kujua', 'assets/topic/Safari_ant_8.jpg', 'Siafu Safari anaweza akasafiri umbali wa mita 20 ndani ya lisaa. Sio Mbaya… Siafu safari wanachukuliwa kuwa wadudu wauaji zaidi duniani. Wanaweza kumla binadamu na kumla binadamu akabaki mifupa kwa muda wa masaa 4. Kuwa na tabia ya kuhamahama, hawa wadudu huhamia sehemu mpya kila siku. Hawa wadudu wadogo wana taya imara sana. Mmoja anaweza akaivuta mara mbili bila kupoteza kushikilia kwake. Siafu safari wa kiume hujulikana kama Nzi Soseji. Huitwa jina hilo kwasababu ana tumbo lililovimbiana.', NULL);
