INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Crocodile', 2, 'Mamba', 'assets/topic/crocodile.svg', 'Ni nani asiyemtambua hayu mnyama wa majini? Mamba……….hawaogopeshi!?. Mamba ni wanyama watambaao, na ni mojawapo wa wanyama wala nyama wanaoogopesha zaidi duniani. Kuna zaidi ya aina 23 tofauti za Mamba duniani. Wanyama hawa wa kuogopesha ni mojawapo ya viumbe vya kale duniani. Wameakuwepo duniani tangu miaka milioni 240 iliyopita. Ni wanyama wa kale haswa!', 'FFB3C8FF');
