INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Crocodile_11', 3, 'Kupata kujua mnyama pori mla nyama', 'assets/topic/crocodile_7.jpg', 'Dubwana kubwa mwenye kuogopesha kwenye maji ni mnyama mla nyama. Kula kwake nyama, hufurahia kula ndege, samaki na hata mamalia. Wakati mwingine hushambulia binadamu pia. Mamba watoto pia ni windo rahisi kwa Mnyama huyu. Inaonekana kushangaza ila ni ukweli. Hawahurumii mamba wadogo pia. Ukiachilia mbali kuwekwa katika kundi la reptilia, mamba wana ufanano wa karibu zaidi na Dinosau. Inaaminika waliishi na dinosau na ndege. Hatuwaoni tena dinosau lakini Mamba bado wanaishi. Hii inastaajabisha kweli.', NULL);
