INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Lion_11', 3, 'Historia ya Simba', 'assets/topic/pexels-photo-627667.jpeg', 'Mfalme mkuu wa msitu alivamia mara moja sehemu kubwa ya afrika na baadhi ya sehemu za asia na Ulaya. Leo wanapatikana Sahara Ndogo ya Afrika. Idadi ndogo sana ya Simba wa Asia wamesalimika India. Simba waliongezeka Afrika kati ya miaka milioni 1 na 800,000. Simba mara kwa mara wanapungua lakini hawawi hatarini.Tunatumaini mfalme wetu wa msitu atabaki na kuendelea kuongezeka kidadi.', NULL);
