INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Lion_21', 3, 'Tuelewe hila ya mwili', 'assets/topic/pride-48373__340.png', 'Mfalme wa Ajabu ana misuli, Paka mwenye kifua kikubwa. Ana kichwa kifupi cha duara, shingo fupi na masikio ya duara. Kuna kifundo cha nywele mwishoni mwa mkia. Mfalme ni mkubwa kuliko malkia. Mfalme ana uzito kati ya kilo 150 na 250. Kwa upande wa urefu Simba dume ni mrefu kuliko simba jike. Malkia ana uzito kati ya kilo 120 hadi 182. Simba dume ana ukuu. Kiumbe kikuu kina misha mafupi. Wanaishi takribani miaka 15. Lakini simba waliohifadhiwa wataendelea kuishi miaka 30.', NULL);
