INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Lion_34', 3, 'Simba na uwindaji wao', 'pexels-photo-627667', 'Simba dume hulinda maeneo yao. Eneo lao lote ni maili 100 za mraba za nyika au mapori ya wazi.Wanyama hawa huweka alama eneo kwa mikojo. Hunguruma kutishia wanyama wengine wanaojaribu kutembea kwenye maeneo yao. Hukimbiza hata wanyama wengine walio kwenye maeneo yao. Simba jike anafahamika kama malkia. Simba jike huwindia kundi la simba dume. Kundi la simba dume hufahamika kama Fahari. Simba jike hufanya kazi pamoja kuwinda Windo. Fahari hufurahia windo lao kwa usawa. Huwagawia na simba wadogo. Wadogo hao huitwa simba Shibli. Shibli hawasaidii kuwinda hadi wafikishe umri wa mwaka 1. Simba hujulikana kwa kufanya kazi pamoja. Wao ni paka pekee wanaobaki kwa Ufahari.', NULL);
