INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Lion_51', 3, 'Ukweli uvutiao kumuhusu mfalme simba', 'assets/topic/Unknown.jpg', 'Kila simba ana mfumo wa ndevu wa kipekee katika kinywa chake. Fikiria idadi ya mifumo! Muungurumo wa simba unaweza kusikika hadi umbali wa maili 3.Hiyo si sauti kubwa!? Mlio wa kutisha! Simba mwenye uzito mkubwa katika rekodi ana uizto wa kilo 375. Huo ni uzito mwingi sana! Simba jike hula nyama kilo 5 kila siku. Simba dume hula nyama kila 7 kila siku. Sasa, hiyo huitwa mlo mzito. Simba jike ni mama mwenye kujali. Antunza hata watoto simba waliotelekezwa. Simba huua chochote kile kuanzia panya, mijusi hadi wanyama pori. Hii ni kweli usalimikaji. Baada ya kubeba mimba kwa siku 105, hatimaye simba jike hujifungua watoto simba 2-3.', NULL);
