INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Africanis_26', 3, 'Muonekano unaopendwa wa Afrikanis', 'assets/topic/africanisdog_9.jpg', 'Mbwa mara nyingi huchukuliwa kuwa rafiki mzuri wa Binadamu. Hii ni kweli kabisa pale inapokuja kwa jamii ya Mbwa Afrikanis. Kuwa rafiki pamoja na asili Michezo, huyu mbwa ni mnyama ni sahihi kabisa kwa nyumbani. Asili yake ya michezo inamfanya awe rafiki mzuri kwa watoto. ukiachana na kuwa na michezo, ni mungalifu na hutoa rahadhari. Nani hapendi kuwa huru?. Ndio, Sawa kabisa na sisi. Afrikanis pia hupenda kuwa huru. lakini muda huo huo huishi kwa adabu na mmiliki wake. Hata na hivyo anakua na ngozi yenye harara na hasira anapozeeka. Pamoja na yote kila mtu hali yake hubadilika.', NULL);
