INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Africanis_48', 3, 'Kuna ulazima wa kujua ukweli juu ya Afrikanis', 'assets/topic/africanisdog_3.jpg', 'Afrikanis wanahisi na ndio maana wana miili laini. Uhai wao ni wastani wa miaka 10 hadi miaka 12. Maisha Mafupi sana!. Mbwa Bantu, Mbwa muwindaji hottentot, Mbwa zulu, Mbwa khoikhoi, haya ni majina mengine ya Mbwa Afrikanis. Afrikanis ni Mbwa mwenye akili na mwenye asili ya urafiki. Afrikanis ni aina ya Mbwa wenye umbo la kati. Afrikanis wanachukua kuwa Ni mbwa wazuri kwa uwindaji. Afrikanis wana nywele fupi na hawahitaji uangalizi wowote. Je!, si rahisi kumtunza mbwa Afrikanis?', NULL);
