INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Elephant_25', 3, 'Tujue kumuhusu Tembo', 'assets/topic/elephant-family-amboseli-260nw-305729312.jpg', 'Kuwa mamalia mkubwa wa ardhini, duniani, tembo wanaendelea kuwa katika orodha ya juu. Uzito wa tembo ni hadi kilo 6000, na kiasi cha urefu wa hadi futi 14. Chukua dakika kumfikiria tembo… Huo ni ukubwa kweli!. Tembo wana mkonga wenye ustadi ambao hufanya kazi zote muhimu. Ana masikio mawili makubwa kweli na pembe mbili zilizojikunja. Mnyama huyu mkubwa na rafiki ana mkia mdogo ambao una urefu wa mita 1.3. Kipimo cha maisha ya tembo wa kiafrika ni miaka 60 hadi 70. Kipimo cha maisha ya tembo wa ki Asia ni miaka 48.', NULL);
