INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Elephant_30', 3, 'Tufanye safari kwenye hisia zao', 'assets/topic/elephant-111695_960_720.jpg', 'Tembo hutumia mkonga wake kwa kunusa , kupumua, kugundua mitetemo, kupapasa watoto wao, kunyonya maji na kushika vitu. Hiyo sio yenye kushangaza!? Sambamba na mkonga wake, masikio yale pia hutumika kwenye madhumuni mengi. Tembo hutumia masikio yao kuwasiliana. Kupigiza masikio yao inaweza kuonyesha shari au furaha. Tembo mmoja huweza kusikia muito wa tembo mwingine kutoka umbali wa mita 4. Viumbe hawa wazuri wana hisia kubwa za kunusa. Tembo huungana na kila mmojawapo mara nyingi kwa hisia ya kugusa. Hutumia viungo vyao vyote vya mwili kuathiriana na kila mmoja. Lakini linapokuja swala la macho, huwa na maono ya wastani.', NULL);
