INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Elephant_42', 3, 'Jaribu kujua kuhusu makazi yao', 'assets/topic/female-314976__340.jpg', 'Tembo wa kike hujulikana kama ng`ombe. Tembo wa kiume hujulikana kama ndovu. Tembo jike huishi katika kundi la familia pamoja na watoto wao. Tembo dume hupendelea kuzurura peke yao. Tembo hubeba ujauzito kwa muda mrefu kuliko mnyama mwingine yeyote. Mimba yake hukaa karibia miezi 22. wakati wa kujifungua tembo mtoto huwa na uzito wa kilo 100 na husimama wima kwa futi 3. Kuna aina mbili za tembo. Tembo wa kiafrika na tembo wa kiasia. Tembo wa kiafrika ni wakubwa kiumbo kulinganisha na tembo wa kiasia.', NULL);
