INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Goat_11', 3, 'Asili ya Mbuzi', 'assets/topic/goat_8.jpg', 'Mbuzi na Binadamu wana Historia ndefu na ya faida pamoja. Ufugaji mkubwa wa Mbuzi ulienea Miaka ya 11,000 iliyopita Kaskazini-mashariki. Kuna aina mbili za Mbuzi, ambao ni Mbuzi wa Milimani na Mbuzi wa kufugwa. Mbuzi wa Milimani. Mbuzi wa Milimani wanapatikana katika milima ya Miamba, maeneo ya Alaska, Montana Magharibi, Idaho kati, Dakota Kusini, Kolorado na Washington. Mbuzi wakufugwa wanakuzwa duniani kote takribani katika kila Nyumba.', NULL);
