INSERT INTO `card` (id, type, title, header, content, option) VALUES ('Goat_27', 3, 'Tuijue familia ya Mbuzi', 'assets/topic/goatfamily_9.jpg', 'Mbuzi wa kiume huitwa Beberu. Mbuzi wa kike huitwa mbuzi jike. Mbuzi mtoto huitwa Kimbalelo. Mbuzi jike hubeba mimba kwa muda wa siku 150 had siku 180 kabla ya kujifungua. Watoto wa Mbuzi ni wachangamfu na wapo kasi. Mara tu baada ya kuzaliwa husimama na kutembea. Watoto wa Mbuzi huhesabika wamekua mara watimizapo miezi 30. Kuna aina 200 za Mbuzi wa kufugwa. Uzito wa Mbuzi wa kufugwa ni kati ya kilo 9 had kilo 113 kulingana na aina ya Mbuzi. Uzito wa Mbuzi wa milimani ni kati ya kilo 57 hadi kilo 82. Mbuzi wana muda mfupi wa kuishi. Mbuzi wa milimani huishi miaka 9 hadi miaka 12. Mbuzi wa kufugwa huishi kati ya miaka 10 hadi miaka 18. Je! familia ya Mbuzi si ya kawaida kama Mbuzi mwenyewe?', NULL);
